import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import { NavLink } from 'react-router-dom';
import { Knob } from "react-rotary-knob";
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';
import Container from 'react-bootstrap/Container';
import ListGroup from 'react-bootstrap/ListGroup';
import '../App.css';
import './knob4.css';
import logo from '../logo.svg';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faCaretUp,
  faCaretDown,
  faCaretLeft,
  faCaretRight,
  faTimesCircle,
  faCompass,
  faCode,
  faCodeBranch,
  faArchive,
  faExternalLinkAlt,
  faEllipsisH,
} from '@fortawesome/free-solid-svg-icons';

import Controls from './controls'


class Knob4 extends Component {

  state = {
    selectedRing: null,
    rot: 45,
    rotation: 'rotate('+45+'deg)',
    style: {
      transform: 'rotate(45deg)',
    },
    rotClass: 'rotOne',
  }

  constructor(props) {
    super(props);
    this.rot = this.state.rot;
    this.canvasRef = React.createRef();
  }

  componentDidUpdate() {
  }

  componentDidMount() {
    document.addEventListener("keypress", e => {
      console.log('gotcha',e.key);
    });

    const canvas = this.canvasRef.current;
    const context = canvas.getContext('2d');
    const img = this.refs.image;

    img.onload = () => {
      context.drawImage(img,0,9);
    }
  }

  componentWillUnmount() {

  }

  controlsInput = (args) => {
    console.log('controlsInput',args);
    if (args.type = 'select') {
        this.selectRing(args.value);
    } else if (args.type = 'rotate') {
      this.rotateRing(args.value)
    }
  }

  selectRing = (args) => {
    console.log('selecting ring',args);
  }

  rotateRing = (args) => {
    console.log('rotating ring',args);
    if (args === 'right') {
      let newRot = this.state.rot+30;
      let newRotation = 'rotate('+newRot+'deg)';
      this.drawRotated(newRot)
      // this.setState({
      //   rotClass: 'rotTwo'
      //   // rot: newRot,
      //   // rotation: newRotation,
      // })
    }
    if (args === 'left') {
      let newRot = this.state.rot-30;
      let newRotation = 'rotate('+newRot+'deg)';  this.drawRotated(newRot)

      // this.setState({
      //   // rot: newRot,
      //   // rotation: newRotation,
      // })
    }
  }

  drawRotated = (degrees) => {
    const canvas = this.canvasRef.current;
    const context = canvas.getContext('2d');
    const img = this.refs.image;

    context.clearRect(0,0,canvas.width,canvas.height);
    context.save();
    context.translate(canvas.width/2,canvas.height/2);
    context.rotate(degrees*Math.PI/180);
    context.drawImage(img,0,3);
    context.restore();
}


  render() {

    return (
      <div className="KnobBoxTop">

      <div className="App-header">
        {
          // <img src={logo} className={this.state.rotClass} alt="logo" />
        }
        <canvas
          width="500"
          height="500"
          ref={this.canvasRef}
        />
        <img src={logo} className={this.state.rotClass} ref="image" alt="logo" />

        <Controls
          controlsInput={this.controlsInput}
        />
      </div>

      </div>
    )
  }

}

export default Knob4;
